import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';
import Draggable from 'react-draggable';

class App extends React.Component {

  componentDidMount() {
    // const jQuery = document.createElement("script");
    // const jQueryUI = document.createElement("script");
    // const jQueryCSS = document.createElement("link");
    // jQuery.src = "https://code.jquery.com/jquery-1.10.2.js";
    // jQueryUI.src = "https://code.jquery.com/ui/1.10.4/jquery-ui.js";
    // jQueryCSS.href = "https://code.jquery.com/ui/1.10.4/themes/ui-lightness/jquery-ui.css";
    // jQueryCSS.rel = "stylesheet";
    // jQueryCSS.async = true;
    // jQuery.async = true;
    // jQueryUI.async = true;
    // document.body.appendChild(jQueryCSS);
    // document.body.appendChild(jQuery);
    // document.body.appendChild(jQueryUI);
  }

  constructor(props) {
    super(props);
    this.state = {
      MainContainer: {backgroundColor: "#282c34", display: "flex", minHeight: "100vh"},
      ObjectMap: [],
    }
    this._createObject = this._createObject.bind(this);
    this._createBox = this._createBox.bind(this);
    this._buttonCreateBox1 = this._buttonCreateBox1.bind(this);
    // this._styleTag = this._styleTag.bind(this);
    // this._draggable = this._draggable.bind(this);
    // this._styleTag();
    // this._draggable();

  }




  // _styleTag = () => {
  //   const styleTag = document.createElement("style");
  //   const styleNode = document.createTextNode( '#draggable {background-color: red; width: 300px; height: 300px;}');
  //   styleTag.appendChild(styleNode);
  //   styleTag.async = true;
  //   document.body.appendChild(styleTag);
  // }

  // _draggable = () => {
  //   const draggable = document.createElement("script");
  //   draggable.onload = ($) => {
  //     $(function() {
  //       $( "#draggable" ).draggable();
  //    });
  //   };
  //   draggable.async = true;
  //   document.body.appendChild(draggable);
  // }

  _dragTask = (event) => {
    this.setState({
      positionX: event.pageX,
      positionY: event.pageY
    })
  }

  _createObject = (object) => {
    var ObjectMap = this.state.ObjectMap;
    ObjectMap.push(object);
    this.setState({ObjectMap: ObjectMap});
  }

  _createBox = (style) => {
    var object = {position: "absolute", top: this.state.positionY, left: this.state.positionX};
    var styleObject = Object.assign({},
      object, style
    )    
    return (
      <div style={styleObject} draggable="true" onDrag={(event) => {this._dragTask(event)}}>

      </div>
      );

  }

  _buttonCreateBox1 = () => {
    this._createObject(this._createBox({backgroundColor: "red", width: "300px", height: "300px"}));
  }

  render() {
    return (
      <div style={this.state.MainContainer}>
        <button type="button" onClick={ this._buttonCreateBox1 }>Click Me!</button>
        {
          this.state.ObjectMap.map((item, index) => {
            return item
          })
        }
      </div>
    );
  }
}
export default App;
